$(function(){$(".del").click(function(t){var a=$(t.target).data("id"),i=$(".item-id-"+a);$.ajax({type:"DELETE",url:"/admin/movie/list?id="+a}).done(function(t){1===t.success&&i.length>0&&i.remove()})}),$("#douban").blur(function(){var t=$(this).val();t&&$.ajax({url:"https://api.douban.com/v2/movie/"+t,cache:!0,type:"get",dataType:"jsonp",crossDomain:!0,jsonp:"callback",success:function(t){$("#inputTitle").val(t.alt_title),$("#inputDoctor").val(t.attrs.director[0]),$("#inputCountry").val(t.attrs.country[0]),$("#inputLanguage").val(t.attrs.language[0]),$("#inputPoster").val(t.image),$("#inputYear").val(t.attrs.pubdate[0].slice(0,4)),$("#inputSummary").val(t.summary)}})})});